	public static int contarCaracteres(String cadenaTexto, char carater) {
		int pos = 0;
		int contador = 0;
		pos = cadenaTexto.indexOf(carater);
		while (pos != -1) {
			contador++;
			pos = cadenaTexto.indexOf(carater, pos + 1);
		}
		return contador;

	}

	public static void contarNegativo(String cadenaTexto) {
		int h = 0;
		for (int i = 0; i < cadenaTexto.length(); i++) {
			char cadea = cadenaTexto.charAt(i);
			if (cadea == '(') {
				h++;
			} else {
				h--;
				if (h == -1) {

					System.out.println("Primera posicion en la que alcanza el -1: " + cadenaTexto.indexOf(')', i+1));
					break;
				}
			}
		}
	}
